INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (1, '2024-01-17 17:06:44', null, '2024-01-17 17:06:44', 'Jeju', '섬 전체가 하나의 거대한 관광자원인 제주도. 에메랄드빛 물빛이 인상적인 협재 해수욕장은 제주 대표 여행지며, 파도가 넘보는 주상절리와 바다 위 산책로인 용머리 해안은 제주에서만 볼 수 있는 천혜의 자연경관으로 손꼽힌다. 드라마 촬영지로 알려진 섭지코스는 꾸준한 사랑을 받고 있으며 한라봉과 흑돼지, 은갈치 등은 제주를 대표하는 음식이다.', '제주', '33.393063', '126.567977', 0, 'Asia/Seoul', 1);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (2, '2024-01-17 17:06:44', null, '2024-01-17 17:06:44', 'Hawaii', '하와이는 연중 온화한 날씨로 사계절 언제 방문해도 좋은 휴양지다. 단 비가 많이 내리는 우기를 피하고 싶다면 강수량이 가장 적은 4월부터 6월까지가 오아후를 여행하기 가장 좋은 시기 물놀이가 여행의 주목적이라면 우기인 11월부터 3월까지는 피하는 것이 좋다. 특히 연중 가장 많은 비가 내리는 11월을 조심할 것', '하와이', '21.476306', '-157.983403', -19, 'Pacific/Honolulu', 2);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (3, '2024-01-17 17:06:44', null, '2024-01-17 17:06:44', 'Guam', '괌의 환상적인 노을과 별로 반짝이는 밤하늘, 굽이치는 산과 계곡은 그리 멀지 않은 곳에서 최고의 경험을 선사할 것입니다. 또한, 육지와 바다에서 즐길 수 있는 황홀한 모험을 즐겨 보시기 바랍니다. 식민지 시대 이전의 문화와 유적지, 마젤란의 배가 처음으로 정박한 곳에서부터 스페인 요새와 2차 세계대전 전투지에 이르기까지 4000년의 아주 흥미로운 역사를 가지고 있는 괌은 많은 이야기를 담고 있습니다.', '괌', '13.444586', '144.768065', 1, 'Pacific/Guam', 2);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (4, '2024-01-17 17:06:44', null, '2024-01-17 17:06:44', 'Saipan', '사이판은 서태평양에 있는 북마리아나 제도의 가장 큰 섬이며, 미국령 북마리아나 제도 연방의 수도입니다. 일광욕, 수중 탐사와 역사 체험까지, 북마리아나 제도에서 가장 크고 개발된 섬인 사이판에서는 모두 가능합니다. 사이판은 황금 해변과 환상의 스쿠버 다이빙이 대표적이지만 멋진 레스토랑, 호텔, 나이트라이프 명소, 쇼핑, 기념물과 골프장도 많답니다.', '사이판', '15.195108', '145.749498', 1, 'Pacific/Saipan', 2);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (5, '2024-01-17 17:06:44', null, '2024-01-17 17:06:44', 'Barcelona', '가우디의 도시이자 스페인 여행의 중심. 바르셀로나는 남동쪽으로 바다가 보이는 완만한 사면에 자리잡고 있다. 이 지역은 베소스 강과 요브레가트 강 사이에 있는 비옥한 평야지대로 산으로 둘러싸여 기후가 온화하고 쾌적하다.', '바르셀로나', '41.399077', '2.139156', -8, 'Europe/Madrid', 3);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (6, '2024-01-17 17:06:44', null, '2024-01-17 17:06:44', 'Singapore', '여유로운 분위기, 쾌적한 환경, 편리한 대중교통, 다양한 볼거리와 즐길거리로 꾸준히 사랑 받고 있는 여행지 싱가포르. 깨끗하고 치안이 좋아 혼자 여행을 준비하는 사람들에게도 인기 있는 곳이다. 도시가 크지 않아 웬만한 거리는 걷거나 지하철을 이용해 다니기 수월하다. ', '싱가포르', '1.341453', '103.801193', -1, 'Asia/Singapore', 4);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (7, '2024-01-17 17:06:44', null, '2024-01-17 17:06:44', 'Bangkok', '순수함을 간직한 ‘천사의 도시’, 태국 방콕은 전 세계에서 매년 1,600만 명이 방문하는 인기 여행지다. 현지 음식도 우리 입맛에 꼭 맞아 방콕으로 식도락 여행을 떠나는 이들도 많다. 어디를 가도 활기찬 길거리 분위기에 기분도 절로 유쾌해진다. 전통문화와 현대적 분위기가 오묘하게 어울려 있는 것도 방콕의 또 다른 매력 포인트. 꼭 방문해야 할 방콕 인기 명소 및 추천 일정을 참고해 완벽한 방콕 여행 계획을 세워보자!', '방콕', '13.792069', '100.608135', -2, 'Asia/Bangkok', 5);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (8, '2024-01-17 17:06:44', null, '2024-01-17 17:06:44', 'Madrid', '스페인 수도 마드리드에서 즐길 수 있는 다양한 역사, 예술, 음식, 패션, 나이트라이프의 하이라이트 등, 이 도시에는 유럽에서 가장 훌륭한 스페인 및 남미 미술 컬렉션을 소장한 박물관과 미술관들이 펼쳐져 있어요. 수많은 야외 광장에는 18세기로 거슬러 올라가는 역사를 지닌 다양한 보자르 스타일의 건물, 독특한 조각상, 오래된 극장들도 많습니다.', '마드리드', '40.421200', '-3.703925', -8, 'Europe/Madrid', 3);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (9, '2024-01-17 17:06:44', null, '2024-01-17 17:06:44', 'Pohang', '경북 동남부에 위치해 한반도에서 가장 빨리 해가 뜨는 고장으로 알려진 경상북도 포항은 천혜의 해안선을 가진 해양경관의 보고이다. 특히 감포에서 구룡포까지 바닷가 도로에 펼쳐지는 풍광은 세계적인 미항인 나폴리나 시드니를 능가하는 아름답고 환상적 해양자원이다. 북부, 칠포해수욕장을 비롯한 여러 해수욕장이 있고 국립 등대박물관, 호미곶 등 해양관광자원은 포항의 대표적 관광자원이다.', '포항', '36.019184', '129.343292', 0, 'Asia/Seoul', 1);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (10, '2024-01-17 17:06:44', null, '2024-01-17 17:06:44', 'Ulleung', '수백만 년 전 자연의 모습을 고스란히 간직한 울릉군. 겨울철 설경이 뛰어난 나리분지와 울릉도의 숨은 비경인 관음도는 약 14m 높이의 관음쌍굴을 보기 위해 많은 이들이 발걸음 한다. 생김새에 따른 이색 암석 형상들도 눈에 띄는데, 새끼를 등에 업은 거북바위부터 바닷물을 들이키는 코끼리바위, 영지버섯 모양의 버섯바위까지 다양해 바위 기행은 이곳에서만 즐길 수 있는 특별한 여행 테마이다. 자원의 보물 창고인 독도도 꼭 한번 다녀와야 할 여행지!', '울릉도', '37.506800', '130.856767', 0, 'Asia/Seoul', 1);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (11, '2024-01-17 17:06:44', null, '2024-01-17 17:06:44', 'Jecheon', '문화재의 집합소 충북 제천시. 청풍문화재단지를 시작으로 국내에서 가장 오래된 저수지 의림지를 비롯해 한국 천주교 전파의 진원지인 베론성지는 편히 둘러보기에도 좋다. 월악산에 둘러싸여 있는 송계계곡은 울창한 숲과 깊은 계곡이 있어 여름철 피서지로 인기가 높다.', '제천', '37.142106', '128.200900', 0, 'Asia/Seoul', 1);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (12, '2024-01-17 17:06:44', null, '2024-01-17 17:06:44', 'Chuncheon', '경춘선 청춘열차와 함께 일상 속 한 발짝 더 가까워진 낭만도시 춘천. 춘천의 대표 여행지로 손꼽히는 남이섬은 사계절마다 다채로운 모습으로 늘 새로운 공간을 연출하고, 김유정 작품의 무대가 되었던 실레마을을 걷다 보면 점순이 등 작품 속 인물들이 마중 나올 것만 같다. 자연 속에 파묻힌 문학과 예술의 숨결을 느끼고 싶다면 춘천 청춘열차에 올라보자.', '춘천', '37.870696', '127.730118', 0, 'Asia/Seoul', 1);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (13, '2024-01-17 17:06:44', null, '2024-01-17 17:06:44', 'Daejeon', '다양한 테마 여행이 가능한 대전광역시. 맨발로 걸을 수 있는 계족산 황톳길은 온몸으로 자연을 느끼는 여행을 할 수 있으며, 대전 근현대 전시관과 남간정사 등 대전에 있는 역사 문화 공간을 코스로 잡아도 좋다. 아이들이 좋아하는 동물원이 있는 오월드와 가볍게 산책하기 좋은 유림공원도 있어 주말 가족 나들이 코스로도 손색이 없다.', '대전', '36.340454', '127.391201', 0, 'Asia/Seoul', 1);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (14, '2024-01-17 17:06:44', null, '2024-01-17 17:06:44', 'Namwon', '춘향의 사랑이 남겨진 남원. 남원의 대표 광한루는 춘향과 몽룡의 사랑이 시작된 곳으로 연못 위에 세워진 오작교가 운치를 더한다. 5가지의 테마로 꾸며진 춘향 테마파크와 작가 최명희의 대하소설의 무대가 되었던 <혼불> 문학관도 가볼 만하다.지리산 바래봉은 철쭉제가 열리는 봄에 여행하기 좋다.', '남원', '35.411732', '127.392206', 0, 'Asia/Seoul', 1);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (15, '2024-01-17 17:06:44', null, '2024-01-17 17:06:44', 'Yeosu', '국제 해양관광의 중심 전남 여수시. 3천여 그루의 동백나무로 가득 찬 붉은 섬 오동도는 웰빙 트래킹 코스를 갖추고 있어 한층 더 운치 있다. 해상 케이블카를 타면 마치 바다 위를 걷는 듯한 느낌이 들며 탁 트인 바다 전망을 감상할 수 있다. 노래 가사에도 나오는 낭만적이고 황홀한 여수의 밤바다는 돌산대교와 음악분수가 함께 어우러져 멋진 야경을 선사한다. 공식 밥도둑 게장백반과 돌산 갓김치, 갈치조림 등 풍부한 먹거리까지 갖춘 인기 만점 관광지!', '여수', '34.759550', '127.672450', 0, 'Asia/Seoul', 1);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (16, '2024-01-17 17:06:44', null, '2024-01-17 17:06:44', 'Busan', '우리나라 제2의 수도 부산광역시. 부산 대표 관광지로 손꼽히는 해운대는 밤에는 마린시티의 야경이 더해져 더욱 화려한 해변이 된다. 감천문화마을은 사진 찍기에 좋으며, 매해 가을마다 개최되는 아시아 최대 규모의 영화제인 부산국제영화제와 함께 부산의 구석구석을 즐겨보는 것도 좋다. 전통시장 투어가 있을 만큼 먹거리가 가득한 부산의 맛기행은 필수!', '부산', '35.195122', '129.089257', 0, 'Asia/Seoul', 1);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (17, '2024-01-17 17:06:44', null, '2024-01-17 17:06:44', 'Gangneung', '은은한 커피향이 남다른 강원도 강릉시. 그중에도 카페거리로 유명한 안목해변은 발이 닿는 어디든 향긋한 커피 한 잔에 지평선 끝까지 펼쳐지는 바다 풍경은 덤으로 얻을 수 있다. 일출 명소로 유명한 정동진과 야경이 아름다운 경포대는 대표 여행 코스! 구름도 머물다 간다는 해발 1,100m에 위치한 강릉 안반데기 마을은 전망대에 올라 드넓게 펼쳐진 배추밭이 붉게 물드는 일출 전경이 일품이다.', '강릉', '37.752659', '128.884601', 0, 'Asia/Seoul', 1);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (18, '2024-01-17 17:06:44', null, '2024-01-17 17:06:44', 'Jeonju', '한국의 멋이 살아있는 전주. 도심 한복판에 자리한 한옥마을에 들어서면 시대를 거슬러가는 기분이다. ''경사스러운 터에 지어진 궁궐''이란 의미의 경기전에 들어서면 대나무가 서로 부대끼며 내는 소리에 귀 기울이게 된다. 전주 야경투어 명소의 대표인 전동성당과 한옥마을을 한눈에 내려다볼 수 있는 오목대 역시 빼놓을 수 없는 곳. 마을 전체가 미술관인 자만 벽화마을은 전주의 대표 포토 존이다.', '전주', '35.829253', '127.128853', 0, 'Asia/Seoul', 1);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (19, '2024-01-17 17:06:44', null, '2024-01-17 17:06:44', 'Gyeongju', '지붕 없는 박물관 경주. 경주는 그만큼 발길이 닿는 어느 곳이든 문화 유적지를 만날 수 있는 곳이다. 밤이면 더 빛나는 안압지를 비롯해 허허벌판에 자리를 굳건히 지키고 있는 첨성대. 뛰어난 건축미를 자랑하는 불국사 석굴암까지 어느 하나 빼놓을 수 없다. 경주 여행의 기록을 남기고 싶다면 스탬프 투어를 이용해보는 것도 좋다. 16곳의 명소를 탐방할 때마다 찍히는 도장 모으는 재미가 쏠쏠하다. 모바일 앱으로도 스탬프 투어 참여가 가능하다.', '경주', '35.844893', '129.215557', 0, 'Asia/Seoul', 1);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (20, '2024-01-17 17:06:44', null, '2024-01-17 17:06:44', 'Andong', '마을 전체가 유네스코 세계문화유산에 등재되어 있는 안동 하회마을. 퇴계 이황 선생의 가르침이 남아있는 도산서원과 그가 거닐던 예던길은 한적한 등산 코스로 좋다. 아기자기한 그림으로 채워진 신세동 벽화마을과 환상적인 야경을 볼 수 있는 월영교도 빼놓을 수 없다.', '안동', '36.564970', '128.737051', 0, 'Asia/Seoul', 1);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (21, '2024-01-17 17:08:16', null, '2024-01-17 17:08:16', 'Mokpo', '아름다운 한 폭의 동양화를 연상시키는 유달산에서 다도해의 경관을 한눈에 감상할 수 있다. 때묻지 않은 자연을 간직한 사랑의 섬 외달도는 전국에서 휴양하기 좋은 섬 30위 안에 선정될 만큼 아름다운 바다와 해변이 특징이다. 목포 평화광장 앞 바다에는 음악에 맞춰 빛과 물이 어우러지는 세계 최대의 춤추는 바다분수가 있어 이색적인 볼거리를 제공하고 있다. 갯벌 속의 인삼이라 불리는 세 발 낙지는 목포의 대표적인 토산품 중 하나이며 일부 지역에서만 잡히는 지역 특산품이다.', '목포', '34.808574', '126.409556', 0, 'Asia/Seoul', 1);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (22, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Incheon', '살짝 비릿한 바다향이 매력적인 인천광역시. 지리적 특징을 잘 이용하여 내륙과 해안 지역의 관광이 두루 발달한 곳이다. 대표적인 해양관광지로는 을왕리 해수욕장을 비롯해 문화의 거리가 갖춰진 월미도 등이 있으며, 한국 속 작은 중국이라 불리는 차이나타운도 인천 여행지로 손꼽힌다. 이 외에도 인천 각처에 오랜 역사 유물들이 산재해 있어 역사를 테마로 여행 코스를 잡아보는 것도 좋다.', '인천', '37.466982', '126.699411', 0, 'Asia/Seoul', 1);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (23, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Gunsan', '1930년대 우리나라 근대역사를 고스란히 간직한 도시 군산. 근대문화유산 투어 코스를 따라 걷다보면 곳곳에 남아있는 일본식 주택과 근대건축물들을 쉽게 볼 수 있다. 2.5km 길이의 오래된 철도가 놓인 경암동 철길마을은 출사지로도 유명. 우리나라에서 가장 오래된 빵집, 전국 5대 짬뽕 맛집, 70년 역사의 호떡집 등 군산 맛집 먹방 여행도 추천한다.', '군산', '35.968488', '126.712852', 0, 'Asia/Seoul', 1);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (24, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Suwon', '서울의 축소판이라도고 할 수 있는 경기도 수원시. 서울만큼이나 정치, 경제, 문화,사회 등 다양한 부문에서 두루 발달한 도시이다. 그중에도 고고학적 가치를 지닌 수원화성은 수원의 자랑이며, 화성행궁 열차를 타고 대표 명소를 둘러보는 것도 좋다. 광교호수공원은 야경이 아름다워 밤에도 산책을 즐기는 사람들이 많으며, 수원 통닭골목은 저렴한 가격에 비해 양이 푸짐에 입소문을 타고 전국 각지에서 많은 이들이 찾아오고 있다.', '수원', '37.280434', '127.020700', 0, 'Asia/Seoul', 1);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (25, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Gapyeong', '자연의 정취에 젖어들게 만드는 가평 아침고요수목원. 어디를 둘러봐도 풍경이 가득해 지루할 틈이 없다. 그중에도 가평하면 빼놓을 수 없는 쁘띠프랑스는 프랑스를 떠올리게 하는 이국적인 풍경으로 빨간 지붕이 매력적이다. 또한 매년 가을에 열리는 자라섬 국제재즈페스티벌은 석양과 음악이 어우러질 때까지 재즈의 매력에 흠뻑 취해볼 수 있다.', '가평', '37.837789', '127.453147', 0, 'Asia/Seoul', 1);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (26, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Geojetongyeong', '예술과 음식의 맛에 혼을 빼앗기는 통영. 보물 같은 섬 욕지도와 바다를 품은 장사도 해상공원 등 수려한 자연경관이 가득한 곳으로 많이 알려져 있지만, 시인 백석이 지나간 자리와 통영이 고향인 소설가 박경리의 발자취가 깃들어 있는 곳이기도 하다. 통영의 명물이 되었다는 충무김밥이나 대표 간식 꿀빵은 통영 여행에 빠지지 않는 주전부리 목록이다.', '거제통영', '34.875428', '128.573231', 0, 'Asia/Seoul', 1);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (27, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Yeongwol', '작은 한반도가 숨어있는 영월군. 멀리서도 물속이 훤히 들여다보일 만큼 맑은 평창강이 감싸 안은 선암마을은 한반도를 빼닮아 한반도 지형이라는 이름을 갖고 있다. 이 아름다운 경관에 맞서는 신비로움이 숨어있으니 바로 고씨동굴이다. 약 5억 년 전에 형성된 것으로 추정되는 이 동굴은 석회동굴로 새로운 볼거리가 가득하다. 그 밖에 육지 속 작은 섬 청령포와 여름에는 래프팅을, 겨울이면 얼음낚시를 즐길 수 있는 동강도 인기 관광 코스!', '영월', '37.204816', '128.481882', 0, 'Asia/Seoul', 1);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (28, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Paris', '파리는 “예술의 도시”, “꽃의 도시”라고 불리는만큼 그 문화적 영향을 세계에 지속하고 있으며, 그 결과 세계 최고의 관광 도시로 부상했다. 이 때문에 1989년 유럽 문화 수도로 선정되었다. 매년 약 4,500만 명의 관광객을 유치하고, 그 60%는 외국에서 온 사람들이다.', '파리', '48.854772', '2.352551', -8, 'Europe/Paris', 6);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (29, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'London', '런던에는 런던 탑, 큐 왕립식물원, 웨스트민스터 사원과 세인트 마가렛 성당으로 이루어진 웨스트민스터 궁전, 그리니치 천문대, 본초 자오선, 경도 0°, 그리니치 평균시 등이 있는 그리니치 유적지 총 네 개의 세계유산이 있다. 다른 유명한 랜드마크로는 버킹엄 궁전, 런던아이, 피카딜리 서커스, 세인트 폴 대성당, 타워 브릿지, 빅 벤, 트라팔가 광장, 더 샤드 등이 있다. 또 수 많은 박물관, 갤러리, 도서관, 체육시설, 문화회관이 있는데, 대영 박물관, 내셔널 갤러리, 테이트 모던, 대영 도서관, 웨스트엔드가 대표적이다. 런던 지하철은 세계 최초이자 가장 오래된 철도망이다.', '런던', '51.508999', '-0.128139', -9, 'Europe/London', 7);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (30, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Roma', '로마는 역사적이고 건축적인 경이로움이 가득하기 때문에 즐길거리가 끊이지 않는 곳입니다. 로마처럼 성공적으로 고대 유적지와 궁전을 아우른 도시는 로마 이외에는 없으며, 어디를 가든 살아있는 역사를 만나볼 수 있습니다. ', '로마', '41.894143', '12.521571', -8, 'Europe/Rome', 8);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (31, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Taipei', '타이베이의 다양함과 다채로운 음식의 향연, 훌륭한 자연환경은 많은 여행객의 마음을 사로잡아 타이베이를 아시아에서 가장 인기 있는 여행지 중 하나로 만들어준다. 타이베이에서 휴가를 보낸다면 새로운 로맨스를 일으킬 수도 있는 놓치지 말아야 할 여행지를 시작으로 타이베이와 사랑에 빠질 나만의 수천 가지 이유를 찾아보자.', '타이페이', '25.040181', '121.509802', -1, 'Asia/Taipei', 9);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (32, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Praha', '더 천천히 걷고, 오래 머물며, 깊이 읽음 직한 도시. 유럽 3대 야경으로 꼽히는 프라하의 아름다운 밤! 도시 전체가 잘 만들어진 예술품처럼 예쁜 프라하. 밤이면 불빛이 하나둘 켜지면서 또 다른 느낌의 장면들을 선사해 줍니다.', '프라하', '50.081181', '14.460124', -8, 'Europe/Prague', 10);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (33, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Losangeles', '천의 얼굴을 지닌 도시, 흥미진진함을 선사하는 특색 있는 동네로 가득한 로스앤젤레스로 오세요. 아주 많은 것들이 끊임없이 일어나는 로스앤젤레스는 계속 탐방하고 싶게 만드는 도시입니다.', '로스앤젤레스', '34.056283', '-118.261617', -17, 'America/Los_Angeles', 2);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (34, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Danang', '다낭은 관광지라기보다 휴양지 혹은 리조트 단지라는 말이 더 적합하다. 여타 지역들에 비해 볼거리도 많지 않고 도심 밖으로 곳곳에 흩어져 있어 리조트에 머물며 무료할 때마다 1~2곳씩 다녀오는 것도 좋다. 하지만 관광목적으로 다낭을 방문했다면 다음의 일정을 참고하면 된다. 그 외 관심도에 따라 바나힐 반나절(혹은 1일), 하이반 패스-랑꼬 비치 반나절을 추가하면 된다.', '다낭', '16.053994', '108.218419', -2, 'Asia/Saigon', 11);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (35, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Sydney', '시드니는 활기찬 수도이자 호주에서 가장 큰 도시입니다.  상징적인 시드니 오페라 하우스와 시드니 하버 브리지 에서 맨리비치 (Manly Beach)로 향하는 페리를 타거나, 본다이 비치 에서 해수욕을 즐기고, 역사적인 장소 록스 지역을 거닐어보세요.', '시드니', '-33.821690', '151.051420', 1, 'Australia/Sydney', 12);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (36, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Vladivostok', '블라디보스토크 구경의 시작점은 혁명전사광장. 중앙에는 극동 소비에트 정권 수립을 위해 싸운 병사들을 기념하는 동상이 있다. 왼쪽으로는 벨리돔이라 불리는 연해변경주청사가 위치한다. 전승기념일 5월 9일에는 각종 퍼레이드와 불꽃놀이가 펼쳐지고, 금요일에는 각종 소비제품의 장터가 열린다. ', '블라디보스토크', '43.118095', '131.892511', 1, 'Asia/Vladivostok', 13);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (37, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Newyork', '전 세계에서 가장 트렌디한 도시. 여행자들의 로망과 기대를 120% 충족시켜 주는 여행의 천국.뉴욕은 인종의 용광로라 할 만큼 전 세계 이민자들이 모이는 곳이다. 다양한 인종이 모이는 만큼 작은 도시 안에 많은 나라의 생활방식, 음식들이 그대로 녹아 있고 이러한 다양성으로 도시는 항상 활기차고 생기 있다.', '뉴욕', '40.691915', '-73.960586', -14, 'America/New_York', 2);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (38, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Amsterdam', '북쪽의 베니스라 불리는 암스테르담은 수백 개의 다리가 있는 반원형 운하 위에 만들어진 도시입니다. 많은 사람들이 낮에는 암스테르담의 유명한 박물관과 암스테르담을 상징하는 커피숍에서 시간을 보내고 밤이 되면 광장 주변의 술집과 클럽으로 몰려듭니다. ', '암스테르담', '52.374124', '4.896561', -8, 'Europe/Amsterdam', 14);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (39, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Beijing', '베이징은 중국의 수도입니다. 약 3천년 전 건설된 도시로, 역사적으로 800년이 넘는 시간 동안 수도의 역할을 수행하고 있습니다. 길고도 깊은 역사와 문화를 자랑하는 이 도시를 방문해보세요. ', '베이징', '39.912218', '116.411160', -1, 'Asia/Shanghai', 15);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (40, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Shanghai', '상하이는 중국의 과거, 현재, 미래가 한 곳에 모여 있다고 생각하면 쉽다. 타 지역에 비해 훨씬 깨끗하고 정돈되어 있는 등 여행하기 좋은 도시이다. 한 마디로 세련된 이미지이다.', '상하이', '31.256922', '121.479464', -1, 'Asia/Shanghai', 15);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (41, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Wien', '오스트리아의 수도인 비엔나는 화려한 문화와 합스부르크제국 분위기가 남은 관광지, 커피하우스, 아늑한 와인 선술집 등, 다채로운 매력으로 유명합니다.', '비엔나', '48.215567', '16.382113', -8, 'Europe/Vienna', 16);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (42, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Lisbon', '알록달록한 색상의 동화 마을 같은 여유로운 분위기가 인상적인 포르투갈의 수도, 리스본! 이 도시는 대지진을 견뎌 낸 구시가지와 새롭게 조성된 신시가지가 공존하는 도시로 유럽의 과거와 현대의 멋을 경험할 수 있는 매력적인 곳이다. 유서 깊은 트램이 도시 곳곳을 돌아다니며 달콤한 에그타르트와 향긋한 포트와인을 비교적 저렴한 가격으로 만나볼 수 있는 리스본은 바쁜 도시를 벗어나 힐링 여행을 즐기기에 제격인 도시다. ', '리스본', '38.746778', '-9.166260', -9, 'Europe/Lisbon', 17);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (43, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Kualalumpur', '쿠알라룸푸르는 나와 다른 피부, 다른 문화, 다른 생각을 하는 사람들과 어울리는 것에 쉽게 익숙해지는 도시다. 히잡을 쓰고 미소를 건네는 말레이 아가씨들과 인사하고 돌아서면 어느새 중국인 아주머니가 힘차게 냄비를 휘젓고 있고, 떠들썩하게 난을 굽는 인도인의 레스토랑을 나서자마자 금발 서양인이 앉아 신문을 보고 있는 카페로 들어간다.', '쿠알라룸푸르', '3.146655', '101.678671', -1, 'Asia/Kuala_Lumpur', 18);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (44, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Copenhagen', '코펜하겐은 도보나 자전거를 타며 둘러보기 좋은 도시입니다. 자전거 길과 차량이 없는 자갈길 도보가 서쪽의 트렌디한 베스터브로에서 운하를 지나 고급 크리스티안스하은 지구까지 이어지고, 그 사이에는 다양한 하이 스트리트 브랜드와 고급 브랜드를 구경할 수 있는 보행자 전용 쇼핑가인 스트뢰에트가 있습니다.', '코펜하겐', '55.676654', '12.582845', -8, 'Europe/Copenhagen', 19);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (45, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Helsinki', '헬싱키는 바다를 바라보는 경관, 다양한 건축물, 세계적으로 유명한 디자인, 북유럽 요리로 여행자들을 즐겁게 하는 도시입니다. 핀란드의 수도인 헬싱키는 걸어서 둘러볼 수 있을 만큼 작고, 즐길거리가 모여 있는 곳에 많은 유명 호텔이 자리하고 있으며, 19세기에 지어진 독특한 교회들, 유네스코에 등재된 요새, 전통적인 시장 등 볼거리들도 많습니다.', '헬싱키', '60.171133', '24.938264', -7, 'Europe/Helsinki', 20);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (46, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Brussel', '브뤼셀은 벨기에의 진수를 보여주는 수도로, 아르데코 양식의 선술집, 독특한 플랑드르 예술, 우뚝 솟은 고딕 양식의 성당, 고풍스러운 길드 하우스로 가득합니다. 일 년 내내 활발한 이벤트가 펼쳐지는 브뤼셀에서 만개한 꽃들과 건축 기념물, 유서 깊은 문화와 예술이 가득한 공원을 구경하고 유명한 벨기에 초콜릿과 다양한 현지 맥주를 맛보세요.', '브뤼셀', '50.844077', '4.366160', -8, 'Europe/Brussels', 21);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (47, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Berlin', '베를린은 유럽의 가장 상징적인 도시 중 하나입니다. 12세기에 세워진 독일의 수도로 긴 격동의 역사를 지니고 있지만, 지금은 독창성, 기술, 문화가 조화된 세계적인 도시가 되었습니다. 1990년 통일된 베를린은 자랑스러운 역사적인 유산을 잘 보존하고 있어서 가볼 만한 매력적인 관광지가 많습니다.', '베를린', '52.520904', '13.411038', -8, 'Europe/Berlin', 22);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (48, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Frankfurt', '프랑크푸르트라는 이름의 기원은, 게르만족의 한 줄기인 프랑크족이 이 도시에 들어와 살게 되면서, ''프랑크족의 통로''라는 데서 유래했다고 한다. 프랑크푸르트는 현재 국제적인 금융, 경제 중심도시로 발전하였고, 독일에선 5번째 대도시이다. 세계적인 규모의 박람회로는 가을에 열리는 모터쇼와 도서박람회가 유명하다. ', '프랑크푸르트', '50.115423', '8.668483', -8, 'Europe/Berlin', 22);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (49, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Hamburg', '함부르크에는 많은 미술관과 19세기 건축 양식을 선보이는 활기찬 주변 지역 등 즐길거리가 풍부한 곳입니다. 물론 비틀스가 1960년대에 처음으로 출세작을 낸 홍등가가 더 유명합니다. 그 거리에는 이 상징적인 브리티시 록밴드의 포스터와 기념품으로 장식된 바들이 있으며, 카바레 극장과 호기심을 불러일으키는 에로틱 미술관도 있습니다.', '함부르크', '53.535840', '10.014920', -8, 'Europe/Berlin', 22);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (50, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Munich', '뮌헨은 독일 남부에 있는 바이에른 주의 최대도시이고 독일에서 베를른, 함부르크 다음 세번째도시입니다. 전쟁 후 재건을 한 이후 역사적 건물과 현대적 건물들이 조화를 이루고 있는 곳입니다. 무엇보다 세계3대축제인 맥주축제 옥토버페스트가 열리는 곳 입니다.', '뮌헨', '48.144084', '11.568597', -8, 'Europe/Berlin', 22);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (51, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Cologne', '쾰른의 즐길거리는 트렌디한 것부터 전통적인 것까지 다양하고, 특히 라인강 서쪽 기슭의 구시가지에 모여 있어요. 이곳의 좁은 자갈길을 따라 도시에서 가장 멋진 술집, 부티크 쇼핑가, 박물관, 유서 깊은 건물을 많이 볼 수 있죠.', '쾰른', '50.939802', '6.967269', -8, 'Europe/Berlin', 22);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (52, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Vancouver', '캐나다 서부 BC 주에서 가장 큰 도시인 밴쿠버는 매년 세계에서 살기 좋은 도시 순위에서 5위 안에 손꼽히는 인기 지역이다. 다양한 인종과 문화가 공존하는 멜팅팟으로 유명하며 사회 전반에 다양한 인종이 진출해 있다.', '밴쿠버', '49.250970', '-123.122540', -17, 'America/Vancouver', 23);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (53, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Toronto', '토론토는 온타리오 주의 주도다. 캐나다에서 가장 큰 도시이며 인구의 반이 캐나다 밖에서 출생한 이민자고, 200여개의 민족이 130여개의 언어와 사투리를 사용한다.', '토론토', '43.705957', '-79.399081', -14, 'America/Toronto', 23);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (54, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Montreal', '캐나다에서 두 번째로 큰 도시로 프랑스어권 도시이다. 북아메리카의 파리로 불리는 도시.', '몬트리올', '45.565313', '-73.720124', -14, 'America/Toronto', 23);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (55, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Nhatrang', '나트랑은 베트남 중부 해안에 자리한 도시입니다. 비교적 한적한 지역으로 깨끗한 해변과 작은 섬, 훌륭한 기념물을 보유하고 있습니다. 나트랑 여행에서는 특별한 것을 수없이 발견하게 된답니다. 나트랑 4개 섬 투어에 참여해 산호초와 활기찬 해양동물 사이에서 수영하고 스노클링을 즐겨보세요. ', '나트랑', '12.250577', '109.196558', -2, 'Asia/Saigon', 11);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (56, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Sanfrancisco', '샌프란시스코는 별난 도시로 심포니, 오페라 및 발레 시즌이 시작되면 무도회와 더불어 포그 콜링 컨테스트를 열어 시선을 끕니다. 유니언 스퀘어부터 노스 비치와 재팬타운까지, 길모퉁이를 돌 때마다 호기심을 자극하는 동네가 나타납니다. 독특하고 창조적이며 매력이 넘치는 샌프란시스코를 직접 경험해보세요.', '샌프란시스코', '37.760872', '-122.429528', -17, 'America/Los_Angeles', 2);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (57, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Salzburg', '세계문화유산 도시인 잘츠부르크에서 호화로운 휴일을! 궁전, 공원, 박물관, 교회, 그리고 자연경관까지, 따분할 틈 없이 여러 면모를 가진 잘츠부르크. 문화에 흠뻑 취하기에도, 쇼핑을 즐기기에도, 거리를 거닐기에도, 커피숍에서 쉬기에도 좋은 잘츠부르크는 다양한 매력을 지닌 도시입니다.', '잘츠부르크', '47.800723', '13.059347', -8, 'Europe/Vienna', 16);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (58, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Nice', '코트 다쥐르 지방의 주도인 니스는 세계적인 휴양지와 관광지로 다양함이 공존한다. 지중해와 산을 사이에 두고 있으며, 이탈리아 국경에서 불과 몇 킬로 떨어진 위치적인 장점을 지니고 있다. 또한 연중 내내 온화한 기후와 햇살, 해변과 다양한 풍경으로 화려한 색감을 자랑한다. 역사, 문화, 예술, 축제, 자연, 미식의 대표 도시인 니스는 다양한 매력이 공존하는 무한한 매력을 지닌 팔레트와 같은 곳이다.', '니스', '43.711246', '7.253680', -8, 'Europe/Paris', 6);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (59, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Zurich', '호숫가 대도시 취리히에는 크리에이티브한 도시 분위기가 찬란한 자연과 함께 어우러져 있다. 셀 수 없이 많은 행사와 다채로운 박물관, 취리히만의 푸드 페스티벌, 스위스에서 가장 생기 넘치는 나이트라이프로 밤낮 없이 활동적이다. 휴식과 휴양을 원한다면 알프스 산이 한 시간 내의 거리에 있다.', '취리히', '47.381453', '8.542126', -8, 'Europe/Zurich', 24);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (60, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Bern', '스위스의 수도인 베른만큼 오랜 역사의 도시를 원형 그대로 보전한 곳은 드물 것이다. 아름다운 구시가는 1983년에 유네스코 세계유산으로 지정되었으며, 비 오는 날에도 우산 없이 편하게 쇼핑을 즐길 수 있는 6km에 이르는 아케이드는 유럽 최장의 길이를 자랑한다. 파울 클레 센터는 스위스 출신의 유명 화가 파울 클레의 작품을 대거 소장하고 있다.', '베른', '46.946027', '7.407460', -8, 'Europe/Zurich', 24);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (61, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Interlaken', '인터라켄은 베르네제 오버란트 지역의 툰 호수와 브리엔츠 호수 사이 비옥한 평원에 자리해 있다. 이 휴양지는 세 개의 웅장한 봉우리, 아이거, 묀히, 융프라우가 기막힌 절경을 선사하며, 수 많은 액티비티를 위한 출발지로 인기다.', '인터라켄', '46.686083', '7.862574', -8, 'Europe/Zurich', 24);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (62, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Geneva', '알프스 봉우리들과 유라 지역의 완만한 언덕 사이에 자리잡은, 불어권 도시 제네바는 제네바 호수로 흘러 들어가는 론 강 만에 자리한다. 따뜻한 인간미의 전통과 국제적인 도시의 향취가 있는 제네바는 UNO 유럽 본부가 위치한 곳이며, 국제 적십자의 본청이 있는 평화의 도시로도 널리 알려져 있다.', '제네바', '46.206572', '6.142115', -8, 'Europe/Zurich', 24);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (63, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Basel', '바젤은 40여 개의 전시 공간이 있는 스위스 내에서 가장 높은 박물관 밀도를 가진 문화 애호가들을 위한 도시다. 다양한 문화뿐만 아니라 휴식처를 제공하는 라인 강이 흐르고 아름다운 구시가와 현대적인 건축물이 공존한다.', '바젤', '47.559916', '7.590515', -8, 'Europe/Zurich', 24);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (64, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Napoli', '활기찬 거리의 삶이 오래되고 무너져 내리는 건물들과 대조를 이루는 나폴리로 로맨틱한 여행을 떠나면 관계에 생기를 불어넣어 줍니다. 나폴리 전역에서 그라피티와 문을 닫은 건물들을 볼 수 있지만 사람들은 친절하고 마음씨가 넓어요. 도시 경치를 구경하기 좋아하는 커플이라면 가치 있는 장면을 많이 찾을 수 있고, 나폴리는 음식도 훌륭하고 멋진 자연 경관에서 조금만 가면 도착할 수 있는 해안에 있어서 로맨틱 여행지로 각광을 받고 있습니다.', '나폴리', '40.855408', '14.252912', -8, 'Europe/Rome', 8);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (65, '2024-01-17 17:08:26', null, '2024-01-17 17:08:26', 'Venice', '베니스는 황홀한 도시로 여행자는 마법 주문에 홀린 듯 베니스에 빠지게 됩니다. 몽환적이고 로맨틱하면서 미스터리와 극적인 분위기가 맴돕니다. 또한 카니발은 일 년에 한 번 열리지만, 카니발의 분위기는 일 년 내내 지속됩니다.', '베니스', '45.440570', '12.343352', -8, 'Europe/Rome', 8);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (66, '2024-01-17 17:08:27', null, '2024-01-17 17:08:27', 'Firenze', '피렌체는 여행객들을 매료시키는 장엄한 예배당과 요새화된 궁전이 조화를 이루고 있으며 르네상스 작품을 소장한 박물관 근처에는 우아한 정원들이 펼쳐져있습니다. 아르노강은 아펜니노 산맥 기슭에 위치한 피렌체를 두 부분으로 나누고 강 위에는 석조 다리인 폰테 베키오가 놓여 있어 이 도시의 양쪽을 모두 감상할 수 있습니다.', '피렌체', '43.779497', '11.246678', -8, 'Europe/Rome', 8);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (67, '2024-01-17 17:08:27', null, '2024-01-17 17:08:27', 'Milano', '밀라노는 이탈리아에서 가장 세련된 도시 중 하나로, 이탈리아의 주요 패션 브랜드 본사가 있는 곳이기도 하며 이 모든 것을 도심 한 곳에서 볼 수 있는 최상의 장소는 세계에서 가장 오래된 쇼핑몰 중 하나인 갤러리아 비토리오 에마뉴엘레 2세입니다. 또한 웅장한 랜드마크들을 통해 풍부한 역사, 예술, 문화를 볼 수 있는 훌륭한 이탈리아 도시 중 하나입니다.', '밀라노', '45.467985', '9.182546', -8, 'Europe/Rome', 8);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (68, '2024-01-17 17:08:27', null, '2024-01-17 17:08:27', 'Antwerp', '벨기에 제 2의 도시, 안트베르펜(Antwerpen), 안트베르펜은 유행을 선도하는 패션의 도시이자 전세계 다이아몬드 거래량의 70%가 이루어지는 도시이며, 건축과 미술을 자랑하는 도시입니다.', '앤트워프', '51.256328', '4.374873', -8, 'Europe/Brussels', 21);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (69, '2024-01-17 17:08:27', null, '2024-01-17 17:08:27', 'Moscow', '러시아의 수도이자 정치, 경제, 문화의 중심지. 모스크바는 옛 러시아 제국, 구 소련, 그리고 현대의 건축물이 모두 모여있는 도시인데 이 건축물들은 조화를 이루며 성 바실리 성당, 붉은 광장, 크렘린 궁전, 모스크바 지하철이 유명하다.', '모스크바', '55.741559', '37.619416', -6, 'Europe/Moscow', 13);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (70, '2024-01-17 17:08:27', null, '2024-01-17 17:08:27', 'Saintpetersburg', '잠들지 않는 여름, 독특한 기후 현상인 백야를 체험해보자, 상트페테르부르크는 최대 규모의 ''백야 축제''가 열리는 곳. 다양한 공연 및 축제와 함께 백야를 즐겨보자.', '상트페테르부르크', '59.950292', '30.213067', -6, 'Europe/Moscow', 13);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (71, '2024-01-17 17:08:27', null, '2024-01-17 17:08:27', 'Budapest', '부다페스트는 두나강이 남북으로 도시를 가로질러 서쪽이 부다, 동쪽이 페스트로 나뉘어 있다. 부다 지역은 산이 많고 예로부터 왕궁이 있어 고급 주택가가 형성되어 있었다. 페스트 지구는 넓은 평지로 번화가와 상업 지구로 되어 있다. 볼거리는 크게 세 군데로 나눌 수 있는데 마차시 성당과 어부의 요새, 왕궁이 있는 부다 언덕, 세체니 다리와 바찌 거리 등이 있는 페스트 지구 시내 중심 그리고 영웅 광장과 온천이 있는 시민 공원이다. 특별히 박물관을 들어가고 온천과 쇼핑을 하지 않는 경우 4~5시간 정도면 다음의 코스대로 모두 갈 수 있을 것이다.', '부다페스트', '47.483413', '19.079490', -8, 'Europe/Budapest', 25);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (72, '2024-01-17 17:08:27', null, '2024-01-17 17:08:27', 'Istanbul', '이스탄불은 풍부한 역사 때문에 시대를 초월한 매력을 가진 곳이에요. 이스탄불은 역사적으로 비잔티움과 콘스탄티노플이라고 불리기도 했는데요. 이 도시는 여러 고대 제국의 중심점 역할을 했어요. 이러한 제국들의 유적지인 수많은 경이로운 건축물들이 아직도 도시의 중심부에 우뚝 서 있어요. 시간의 시험을 견뎌내고 눈부신 모자이크 장식이 경외감을 불러일으키는 하기아 소피아도 빼놓을 수 없죠.', '이스탄불', '41.016032', '28.981411', -6, 'Europe/Istanbul', 26);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (73, '2024-01-17 17:08:27', null, '2024-01-17 17:08:27', 'Melbourne', '호주 빅토리아 주는 호주 제2의 도시이자 호주 최고의 문화 예술의 도시인 멜버른을 비롯한 세련된 도시 문명과 함께 해변과 바다, 섬, 평야, 계곡, 산맥 등이 어우러진 다채로운 자연 환경을 자랑합니다.', '멜버른', '-37.910289', '145.006241', 2, 'Australia/Melbourne', 12);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (74, '2024-01-17 17:08:27', null, '2024-01-17 17:08:27', 'Brisbane', '브리즈번은 퀸즐랜드주의 주도로, 떠오르는 맛집 체험과 신나는 문화 체험지로 주목받고 있는 세련된 감각의 도시입니다. 일 년 내내 아열대 기후를 유지하는 브리즈번은 온화한 기후를 선호하는 여행자들, 호주 북쪽 지역을 목적지로 잡은 각지의 여행자들과 만나 정보를 교환하고 우정을 쌓고 싶은 여행자들에게 안성맞춤 여행지입니다.', '브리즈번', '-27.454683', '153.027754', 1, 'Australia/Brisbane', 12);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (75, '2024-01-17 17:08:27', null, '2024-01-17 17:08:27', 'Zagreb', '주황색 지붕이 아름다운 크로아티아의 자그레브는 옛날부터 서유럽과 동유럽을 연결하는 가교 역할을 해 온 도시로 유럽인들이 꼭 한 번 방문하고 싶어하는 도시로 손꼽힙니다. 구시가지와 신시가지로 나뉜 도시의 구시가지 쪽에는 중세유럽을 그대로 느낄 수 있는 고풍스러운 건물들과 광장, 탑, 성당 등 명소가 몰려 있어 하루 정도 일정으로 워킹투어를 즐기는 관광객들이 많습니다. ', '자그레브', '45.824361', '15.973236', -8, 'Europe/Zagreb', 27);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (76, '2024-01-17 17:08:27', null, '2024-01-17 17:08:27', 'Athens', '도시와 해변의 매력이 만나는 고대 도시, 한때 신석기 시대에 가장 강력한 문명의 중심지였던 아테네는 아크로폴리스에서 올림피아 제우스 신전에 이르기까지 고대의 거대 건축물의 위업이 사방에 즐비합니다. 하지만, 아테네의 동네와 뒷골목은 기분 좋은 모던함이 묻어나고 느긋한 에너지가 드러나며, 활기찬 도회풍 음식점과 멋진 갤러리가 지나간 시대의 유물들 속에 공존합니다. ', '아테네', '37.983523', '23.732500', -7, 'Europe/Athens', 28);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (77, '2024-01-17 17:08:27', null, '2024-01-17 17:08:27', 'Telaviv', '만약 예루살렘이 이스라엘의 워싱턴, 오타와(Ottawa) 또는 캔버라(Canberra)라면, 텔아비브는 이스라엘의 비즈니스, 엔터테인먼트 및 예술의 중심인 이스라엘의 뉴욕, 토론토 또는 시드니(Sydney)에 해당합니다. 이곳은 또한 9 마일의 해변과 그리고 해변 의자, 우산, 구조요원 탑, 카페, 술집, 바 및 레스토랑이 줄 지어 있는 14 마일 길이의 산책로가 있는  세계에서 몇 안되는 주요 지중해 해변 휴양지 도시 중 하나이기도 합니다.', '텔아비브', '32.081234', '34.786429', -7, 'Asia/Jerusalem', 29);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (78, '2024-01-17 17:08:27', null, '2024-01-17 17:08:27', 'Oslo', '오슬로는 아름다운 오슬로피오르의 해안에 있는 번영한 도시입니다. 잎이 무성간 거리와 구시가지 센터의 광장이 대조를 이루고, 세계를 선도하는 건축 기술로 대담하고 새로운 건축물을 지어내는 이 도시는 과거와 현재가 공존하고 있습니다.', '오슬로', '59.911914', '10.750116', -8, 'Europe/Oslo', 30);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (79, '2024-01-17 17:08:27', null, '2024-01-17 17:08:27', 'Hanoi', '베트남의 수도로, 정치, 경제, 사회, 문화의 중심지이자 과거와 현재가 공존하는 곳입니다. 시선이 닿는 거의 모든 곳에 문화적인 유적지가 존재합니다. 성 요셉 대성당은 노트르담 드 파리를 본 따 건설되었습니다. 성당 곳곳에 역사를 알려주는 다양한 요소가 가득합니다.', '하노이', '21.024020', '105.835676', -2, 'Asia/Saigon', 11);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (80, '2024-01-17 17:08:27', null, '2024-01-17 17:08:27', 'Hochiminh', '호찌민 시티 내 주요 볼거리는 통일궁과 베트남 전쟁박물관, 노트르담 대성당을 비롯한 프랑스식 건축물이다. 그 외 호찌민 미술관, 베트남 역사박물관, 호찌민 시티 박물관, 호찌민 박물관이 있는데 순서대로 방문할 가치가 있다.쇼핑 장소로는 벤탄 시장과 사이공 스퀘어가 대표적이다. 벤탄 시장은 관광객들을 겨냥한 재래시장 업그레이드 버전이며, 사이공 스퀘어는 명품 이미테이션과 메이드인 베트남(나이키, 키플링 등) 제품을 구입할 수 있는 곳이다. 그 외 메콩강, 꾸찌 터널, 붕따우 등 1일 투어를 추가하면 좋다.', '호치민', '10.745107', '106.681481', -2, 'Asia/Saigon', 11);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (81, '2024-01-17 17:08:27', null, '2024-01-17 17:08:27', 'Chiangmai', '치앙마이는 태국에서 두 번째로 큰 도시로 태국 북부의 경제 중심지입니다. 북적북적한 방콕과는 전혀 다른 분위기를 느껴보세요. 상큼하고 조용한 분위기 덕분에 “북쪽에 핀 장미”라고 불리기도 합니다. 란나 왕국의 수도였던 치앙마이에는 매력과 전통이 가득합니다.', '치앙마이', '18.763294', '98.956647', -2, 'Asia/Bangkok', 5);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (82, '2024-01-17 17:08:27', null, '2024-01-17 17:08:27', 'Taichung', '쇼핑과 야시장 먹거리에 홀딱 반할 타이완의 중심이라는 의미를 가진 도시.', '타이중', '24.148610', '120.678115', -1, 'Asia/Taipei', 9);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (83, '2024-01-17 17:06:44', null, '2024-01-17 17:08:27', 'Kaohsiung', '대만 남단에 위치한 거대 컨테이너 항구도시 가오슝, 타이베이시에 이은 대만 제2의 도시이다. 대만의 항구 수도라 할 수 있는 가오슝은 원숭이 산부터 치진 섬 해안까지 아름다운 전망을 갖추고 있으며 활기찬 야시장과 세련된 문화까지 모두 즐길 수 있는 곳.', '가오슝', '22.624077', '120.333815', -1, 'Asia/Taipei', 9);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (84, '2024-01-17 17:08:27', null, '2024-01-17 17:08:27', 'Tainan', '타이베이로 천도전까지 220여 년간 타이완의 수도. 문화 예술의 중심지, 고급스러운 분위기가 느껴지는 타이난에 가신다면 관광, 온천, 유적지 등을 꼭 즐겨보세요! 타이난에서는 타이난 화원 야시장 및 적감루 등의 인기 명소를 둘러보며 이곳 현지의 매력에 흠뻑 빠져보세요.', '타이난', '23.003725', '120.229339', -1, 'Asia/Taipei', 9);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (85, '2024-01-17 17:08:27', null, '2024-01-17 17:08:27', 'Tashkent', '타슈켄트 우즈베키스탄의 수도입니다. 타슈켄트 여행에서 타슈켄트의 핫플레이스라 할 수 있는 브로드웨이를 추천합니다. 관광객과 현지인들이 길거리게임, 자전거 대여, 망원경 별보기서비스, 길거리 화가 등 다채로운 볼거리와 즐길 거리가 있습니다.', '타슈켄트', '41.297785', '69.263242', -4, 'Asia/Samarkand', 31);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (86, '2024-01-17 17:08:27', null, '2024-01-17 17:08:27', 'Manila', '과거와 현재가 날실과 씨실처럼 짜여 있는 이 도시에는 동서양의 문화가 조화를 이루고 있습니다. 한때 스페인의 지배를 받았기 때문에 이곳은 유럽 남서부의 분위기가 만연합니다. 마닐라는 고층 빌딩의 숲입니다. 수많은 거리와 교회와 더불어 정원, 골목, 꽃이 가득한 샛길이 백 년의 역사를 자랑합니다. 마닐라 도심은 마닐라에 뿌리내린 스페인 문화를 감상하기 좋은 곳입니다.', '마닐라', '14.597490', '120.980075', -1, 'Asia/Manila', 32);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (87, '2024-01-17 17:08:27', null, '2024-01-17 17:08:27', 'Vientiane', '비엔티안은 라오스의 수도로 메콩강이 도시를 동서로 가르며 태국과 국경을 마주하고 있습니다. 옛 라오족 왕조 이래 계속 유지되어온 오래된 수도로 1350년대부터 왕국의 중요한 행정도시로서 기하고 있습니다. ''비엔티안''의 사전적인 의미는 ''달이 걸린 땅''이라고 합니다. 이름처럼 달이 뜬 밤의 야경이 멋진 곳으로 개발이 덜 진행되어 자연경관을 보는 것만으로도 다른 곳에서 느낄 수 없는 운치를 체험할 수 있습니다.', '비엔티안', '17.966310', '102.615552', -2, 'Asia/Vientiane', 33);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (88, '2024-01-17 17:08:27', null, '2024-01-17 17:08:27', 'Phnompenh', '프놈펜은 캄보디아의 수도로 정치, 경제, 문화의 중심이다. 중국 티베트에서 발원한 메콩강과 톤레삽 호수에서 발원한 톤레삽 강, 그리고 이 두 강에서 갈라져 나가는 바삭강이 교차하여 X자 모양을 형성하는 지점에 바로 프놈펜이 위치해 있다.', '프놈펜', '11.564548', '104.891900', -2, 'Asia/Phnom_Penh', 34);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (89, '2024-01-17 17:08:27', null, '2024-01-17 17:08:27', 'Seoul', '과거와 현재가 공존하며 하루가 다르게 변하는 서울을 여행하는 일은 매일이 새롭다. 도시 한복판에서 600년의 역사를 그대로 안고 있는 아름다운 고궁들과 더불어 대한민국의 트렌드를 이끌어나가는 예술과 문화의 크고 작은 동네들을 둘러볼 수 있는 서울은 도시 여행에 최적화된 장소다.', '서울', '37.519672', '126.984609', 0, 'Asia/Seoul', 1);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (90, '2024-01-17 17:08:27', null, '2024-01-17 17:08:27', 'Hongkong', '동양과 서양, 과거와 현재의 느낌이 공존하는 역동적인 매력의 도시.', '홍콩', '22.318389', '114.170695', -1, 'Asia/Hong_Kong', 35);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (91, '2024-01-17 17:08:27', null, '2024-01-17 17:08:27', 'Mandalay', '미얀마 마지막 왕조, 꼰바웅 왕조의 수도 만달레이는 지금도 양곤에 이어 미얀마 제2의 도시이자 미얀마 최대의 종교문화의 중심지.', '만달레이', '21.942549', '96.097113', -2, 'Asia/Rangoon', 36);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (92, '2024-01-17 17:08:27', null, '2024-01-17 17:08:27', 'Osaka', '일본의 미식과 역사의 중심지. 도톤보리의 눈부신 불빛 아래, 전통적인 타코야키와 오코노미야키의 맛을 즐기세요. 오사카성에서는 일본의 고대 역사를 체험할 수 있습니다. 유니버설 스튜디오 재팬에서는 환상적인 어트랙션을 경험할 수 있으며, 신세계의 츠텐카쿠 타워에서는 도시의 전경을 한 눈에 볼 수 있습니다. 오사카에서는 끝없는 모험과 발견이 기다립니다.', '오사카', '34.665476', '135.502070', 0, 'Asia/Tokyo', 37);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (93, '2024-01-17 17:08:27', null, '2024-01-17 17:08:27', 'Tokyo', '세계의 여러 도시 중 어디에서도 경험할 수 없는 매력을 지닌 도쿄는 일본의 수도로서 도시의 중심에서 미래의 기술과 과거의 전통이 공존하는 독특한 장소입니다. 도쿄의 번화한 거리와 조용한 사찰, 최첨단의 빌딩과 고요한 정원 사이에서 당신은 진정한 도쿄의 매력을 발견할 수 있습니다.', '도쿄', '35.708134', '139.732551', 0, 'Asia/Tokyo', 37);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (94, '2024-01-17 17:08:27', null, '2024-01-17 17:08:27', 'Kohsamui', '커플 여행자들의 로맨틱한 여행지! 아름답고 평화로운 비치와 로컬 레스토랑의 매력과 로맨틱한 파인 다이닝, 코사무이의 비치들은 수심도 낮고 모래사장도 곱고 깨끗해서 바다를 즐기에 제격이다. 낮에는 조용하지만 저녁이면 해변 레스토랑은 로맨틱한 분위기를 자아내고 해변은 파티로 활기차진다.', '코사무이', '9.513261', '100.013776', -2, 'Asia/Bangkok', 5);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (95, '2024-01-17 17:08:27', null, '2024-01-17 17:08:27', 'Bali', '세계 최고의 휴양지, 세계 1위의 허니문 여행지 신들의 섬 발리! 아름다운 열대 자연과 세계 각지의 입맛을 사로잡는 미슐랭, 남녀노소 즐길 수 있는 휴양지의 매력이 가득한 곳, 보기만 해도 뜨거운 활화산 아궁화산, 칼데라호와 함께 스펙타클한 장관이 일품이 바뚜르 화산. 천혜의 밀림 속 문화와 예술의 도시 우붓 등 볼거리가 많은 신들의 섬.', '발리', '-8.663688', '115.215437', -1, 'Asia/Makassar', 38);
INSERT INTO city (id, created_at, deleted_at, updated_at, english_name, introduction, korean_name, latitude, longitude, time_difference, timezone, country_id) VALUES (97, '2024-01-17 17:08:27', null, '2024-01-17 17:08:27', 'Fukuoka', '일본의 남서쪽에 위치한 활기찬 해변 도시입니다. 텐진의 번화가에서는 최신 패션과 문화를 체험하실 수 있고, 하카타역에서는 도시 간 편리한 이동을 즐기실 수 있습니다. 맛있는 하카타 라멘은 방문객들의 입맛을 사로잡습니다. 오호리 공원의 평온한 풍경에서는 여유를 느낄 수 있으며, 후쿠오카 타워에서는 도시의 아름다운 전경을 감상하실 수 있습니다. 후쿠오카는 휴식과 모험, 그리고 맛있는 음식의 완벽한 조화를 제공합니다.', '후쿠오카', '33.573720', '130.347310', 0, 'Asia/Tokyo', 37);
